import {
  Background,
  CurrentSongActions,
  SET_BACKGROUND,
  SET_SONG,
  Song
} from "../types";

export const setCurrentSong = (data: Song): CurrentSongActions => ({
  type: SET_SONG,
  data
});

export const setBackground = (data: Background): CurrentSongActions => ({
  type: SET_BACKGROUND,
  data
});
