import React, { ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlay,
  faStepBackward,
  faStepForward
} from "@fortawesome/free-solid-svg-icons";
import { Action } from "redux";
import { fetchPlaylist } from "../store/actions";
import { connect, ConnectedProps } from "react-redux";
import { State } from "../store/types";
import { ThunkDispatch } from "redux-thunk";
import ReactAudioPlayer from "react-audio-player";
import {NowPlaying} from "../components";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  fetchPlaylist: ({ id, type }: { id: string; type: "album" | "artist" }) =>
    dispatch(fetchPlaylist({ id, type }))
});

const mapStateToProps = (state: State) => ({
  albumArt: state.currentSong.albumArt,
  isDark: state.currentSong.background?.isDark
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const MusicPlayer: React.FC<PropsFromRedux> = ({
  albumArt,
  fetchPlaylist,
  isDark
}): ReactElement => {
  let audioPlayer: ReactAudioPlayer;
  fetchPlaylist({ id: "5f1423c54ee369215e05625f", type: "album" });

  return (
    <div
      style={{
        display: "block",
        margin: 0,
        position: "absolute",
        height: "100%",
        width: "100%",
        color: isDark ? "white" : "black",
        fontFamily: "'Work Sans', sans-serif",
        fontSize: "18px"
      }}
    >
      <div
        style={{
          height: "5%",
          width: "90%",
          textAlign: "center",
          padding: "5% 5% 2% 5%"
        }}
      >
<NowPlaying />
      </div>
      <div
        className="album-art"
        style={{
          paddingLeft: "10vw",
          paddingTop: "5vh",
          paddingRight: "10vw",
          width: "80vw",
          height: "80vw"
        }}
      >
        <img src={albumArt} width="100%" height="100%"/>
      </div>
      <ReactAudioPlayer
        ref={(element: ReactAudioPlayer) => (audioPlayer = element)}
        // src={currentSong.data ? `/api/song/${currentSong.data._id}` : ""}
        listenInterval={1000}
        onListen={(e) => console.log(e)}
        onLoadedMetadata={(data) => console.log(data)}
      />
      <div
        style={{
          height: "10%",
          width: "90%",
          position: "absolute",
          bottom: 0,
          padding: "4% 5%"
        }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <div style={{ fontSize: "48px" }}>
            <FontAwesomeIcon icon={faStepBackward} />
          </div>
          <div style={{ fontSize: "96px", margin: "0 96px" }}>
            <FontAwesomeIcon icon={faPlay} />
          </div>
          <div style={{ fontSize: "48px" }}>
            <FontAwesomeIcon icon={faStepForward} />
          </div>
        </div>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            marginTop: "48px",
            fontSize: "32px"
          }}
        >
          <div>00:00</div>
          <input type="range" style={{ width: "100%", margin: "0 24px" }} />
          <div>02:40</div>
        </div>
      </div>
    </div>
  );
};

export default connector(MusicPlayer);
