// Constants

export const FETCH_PLAYLIST = "FETCH_PLAYLIST";
export const FETCH_PLAYLIST_FAILURE = "FETCH_PLAYLIST_FAILURE";
export const FETCH_PLAYLIST_STARTED = "FETCH_PLAYLIST_STARTED";
export const FETCH_PLAYLIST_SUCCESS = "FETCH_PLAYLIST_SUCCESS";

// Typescript types

export interface Song {
  title: string;
  artist: string;
  album: string;
  albumArtPath: string;
  song: any;
  songPath: string;
  playlists: string[];
}

export type Status = "PLAYING" | "PAUSED";

export interface CurrentSongState {
  status: Status;
  song: AudioBuffer | null;
  data: Song | {};
}

export type State = CurrentSongState;

// Action types

export interface FetchPlaylistAction {
  type: typeof FETCH_PLAYLIST;
}

export interface FetchPlaylistFailureAction {
  type: typeof FETCH_PLAYLIST_FAILURE;
  payload: {
    error: any;
  };
}

export interface FetchPlaylistStartedAction {
  type: typeof FETCH_PLAYLIST_STARTED;
}

export interface FetchPlaylistSuccessAction {
  type: typeof FETCH_PLAYLIST_SUCCESS;
  payload: Song[];
}

export type PlaylistActions =
  | FetchPlaylistAction
  | FetchPlaylistFailureAction
  | FetchPlaylistStartedAction
  | FetchPlaylistSuccessAction;

export type CurrentSongActions = {};
