import {
  FETCH_SONGS,
  FETCH_SONGS_FAILURE,
  FETCH_SONGS_SUCCESS,
  PLAYLIST_ADD,
  PlaylistActions,
  PlaylistState,
  SET_PLAYLIST,
  UPDATE_PLAYLIST
} from "../types";

const initialState: PlaylistState = {
  previous: [],
  next: []
};

export const playlist = (
  state: PlaylistState = initialState,
  action: PlaylistActions
) => {
  switch (action.type) {
    case FETCH_SONGS:
      return {
        ...state,
        loading: true
      };
    case FETCH_SONGS_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload.error
      };
    case FETCH_SONGS_SUCCESS:
      return {
        ...state,
        loading: false,
        error: null,
        data: action.payload
      };
    case SET_PLAYLIST:
      return {
        previous: [],
        current: action.payload.firstSong,
        next: action.payload.nextSongs
      };
    case PLAYLIST_ADD:
      return {
        ...state,
        next: {
          ...state.next,
          ...action.payload
        }
      };
    case UPDATE_PLAYLIST:
      return {
        ...action.payload
      };
    default: {
      return state;
    }
  }
};
