import React, {
  createRef,
  FC,
  ReactElement,
  SyntheticEvent,
  useEffect,
  useState
} from "react";
import { PlaylistState, State, Tabs } from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { setSelectedTab } from "../../store/actions";
import { updatePlaylist } from "../../store/actions/playlistActions";
import { text } from "@fortawesome/fontawesome-svg-core";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  updatePlaylist: ({ previous, current, next }: PlaylistState) =>
    dispatch(updatePlaylist({ previous, current, next })),
  setSelectedTab: (tab: Tabs) => dispatch(setSelectedTab(tab))
});

const mapStateToProps = (state: State) => ({
  artists: state.artists.data,
  albums: state.albums.data,
  playlists: state.playlists.data
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const SearchTab: FC<PropsFromRedux> = ({
  artists,
  setSelectedTab,
  updatePlaylist,
  albums,
  playlists
}): ReactElement => {
  const [searchTerm, setSearchTerm] = useState("");
  const textInput = createRef<HTMLInputElement>();

  useEffect(() => {
    console.log("changed to: ", searchTerm);
  }, [searchTerm]);

  const handleClick = () => {
    if (textInput.current && textInput.current.value !== searchTerm) {
      setSearchTerm(textInput.current.value);
    }
  };

  return (
    <>
      <div style={{ display: "flex", height: "4vh", fontSize: "36px" }}>
        <input
          ref={textInput}
          type="text"
          placeholder="Type to search..."
          style={{
            width: "calc(100% - 8vh)",
            fontSize: "36px",
            paddingLeft: "2vw"
          }}
        />
        <input
          type="button"
          style={{
            width: "8vh",
            fontSize: "36px"
          }}
          value="Search"
          onClick={() => handleClick()}
        />
      </div>
      {searchTerm === "" && (
        <div
          style={{ fontSize: "48px", textAlign: "center", marginTop: "20px" }}
        >
          Please type something in the search bar to begin.
        </div>
      )}
    </>
  );
};

export default connector(SearchTab);
