// Constants

export const FETCH_PLAYLIST = "FETCH_PLAYLIST";
export const FETCH_PLAYLIST_FAILURE = "FETCH_PLAYLIST_FAILURE";
export const FETCH_PLAYLIST_SUCCESS = "FETCH_PLAYLIST_SUCCESS";

export const SET_SONG = "SET_SONG";
export const SET_BACKGROUND = "SET_BACKGROUND";
export const TOGGLE_PLAYING = "TOGGLE_PLAYING";
export const NEXT_SONG = "NEXT_SONG";
export const PREV_SONG = "PREV_SONG";

// Typescript types

export interface BasicDetails {
  _id: string;
  name: string;
}

export interface Song extends BasicDetails {
  artists: BasicDetails[];
  albums: BasicDetails[];
}

export interface Background {
  colour: string;
  isLight: boolean;
  isDark: boolean;
}

export interface CurrentSongState {
  isPlaying: boolean;
  data?: Song;
  song?: string;
  albumArt?: string;
  background?: Background;
}

export interface PlaylistState {
  error: any;
  loading: boolean;
  data?: Song[];
}

export interface State {
  currentSong: CurrentSongState;
  playlist: PlaylistState;
}

// Action typescript types

export interface FetchPlaylistAction {
  type: typeof FETCH_PLAYLIST;
}

export interface FetchPlaylistFailureAction {
  type: typeof FETCH_PLAYLIST_FAILURE;
  payload: {
    error: Error;
  };
}

export interface FetchPlaylistSuccessAction {
  type: typeof FETCH_PLAYLIST_SUCCESS;
  payload: Song[];
}

export type PlaylistActions =
  | FetchPlaylistAction
  | FetchPlaylistFailureAction
  | FetchPlaylistSuccessAction;

export interface SetSongAction {
  type: typeof SET_SONG;
  data: Song;
}

export interface SetBackgroundAction {
  type: typeof SET_BACKGROUND;
  data: Background;
}

export interface TogglePlayingAction {
  type: typeof TOGGLE_PLAYING;
}

export interface NextSongAction {
  type: typeof NEXT_SONG
}

export interface PrevSongAction {
  type: typeof PREV_SONG
}

export type CurrentSongActions = SetSongAction | SetBackgroundAction | TogglePlayingAction | NextSongAction | PrevSongAction;
