import {CurrentSongActions, CurrentSongState, Status} from "../types";

const initialState = {
  status: "PAUSED" as Status,
  song: null,
  data: {}
};

export const currentSong = (
  state: CurrentSongState = initialState,
  action: CurrentSongActions
) => {
  switch (action.type) {
    case CHANGE_SONG: {
      return action.payload;
    }
    default: {
      return state;
    }
  }
};

export {};
