import React, { FC, ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";
import { State, Tabs } from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { setSelectedTab } from "../../store/actions";
import { shuffleSongs } from "../../store/actions/currentSongActions";
import { IconProp } from "@fortawesome/fontawesome-svg-core";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  setSelectedTab: (tab: Tabs) => dispatch(setSelectedTab(tab)),
  shuffleSongs: () => dispatch(shuffleSongs())
});

const mapStateToProps = (state: State) => ({
  selectedTab: state.menu.selectedTab
});

const connector = connect(mapStateToProps, mapDispatchToProps);

interface TabButtonProps {
  icon: IconProp;
  tabName: Tabs;
}

type PropsFromRedux = ConnectedProps<typeof connector>;

const TabButton: FC<PropsFromRedux & TabButtonProps> = ({
  selectedTab,
  setSelectedTab,
  icon,
  tabName
}): ReactElement => {
  return (
    <div onClick={() => setSelectedTab(tabName)}>
      <FontAwesomeIcon icon={icon} />
      <div
        style={{
          width: selectedTab === tabName ? "200%" : 0,
          height: "8px",
          backgroundColor: "black",
          position: "relative",
          left: "-50%",
          marginTop: "8px",
          transition: "all 0.5s ease"
        }}
      />
    </div>
  );
};

export default connector(TabButton);
