import {
  FETCH_PLAYLIST,
  FETCH_PLAYLIST_FAILURE,
  FETCH_PLAYLIST_SUCCESS,
  PlaylistActions,
  PlaylistState
} from "../types";

const initialState: PlaylistState = {
  loading: false,
  data: [],
  error: null
};

export const playlist = (
  state: PlaylistState = initialState,
  action: PlaylistActions
) => {
  switch (action.type) {
    case FETCH_PLAYLIST:
      return {
        ...state,
        loading: true
      };
    case FETCH_PLAYLIST_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload.error
      };
    case FETCH_PLAYLIST_SUCCESS:
      console.log(Array.isArray(action.payload))
      return {
        ...state,
        loading: false,
        error: null,
        data: [action.payload]
      };
    default: {
      return state;
    }
  }
};
