import React, { CSSProperties, FC, ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faMusic } from "@fortawesome/free-solid-svg-icons";
import { Playlist } from "./";
import { State, Tabs as TabsType } from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { setSelectedTab } from "../../store/actions";
import { shuffleSongs } from "../../store/actions/currentSongActions";
import TabButton from "./TabButton";

const tabButtonStyles: CSSProperties = {
  height: "8px",
  backgroundColor: "black",
  position: "relative",
  left: "-50%",
  marginTop: "8px",
  transition: "all 0.5s ease"
};

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  setSelectedTab: (tab: TabsType) => dispatch(setSelectedTab(tab)),
  shuffleSongs: () => dispatch(shuffleSongs())
});

const mapStateToProps = (state: State) => ({
  selectedTab: state.menu.selectedTab
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Tabs: FC<PropsFromRedux> = ({
  selectedTab,
  setSelectedTab,
  shuffleSongs
}): ReactElement => {
  const tabsData = [
    {
      type: "PLAYLIST" as TabsType,
      icon: faBars
    },
    {
      type: "SONGS" as TabsType,
      icon: faMusic
    }
  ];

  return (
    <div
      style={{
        height: "90vh",
        width: "100vw",
        backgroundColor: "rgba(255, 255, 255, .5)",
        backdropFilter: "blur(20px)",
        position: "relative",
        bottom: 0
      }}
    >
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-around",
          fontSize: "72px",
          width: "90vw",
          height: "10vh",
          margin: "0vh 5vw 1vh 5vw",
          textAlign: "center"
        }}
      >
        {tabsData.map(({ icon, type }) => (
          <TabButton key={`tab-button-${type}`} icon={icon} tabName={type} />
        ))}
        {/*<div onClick={() => setSelectedTab("ARTISTS")}>*/}
        {/*  <FontAwesomeIcon icon={faUsers} />*/}
        {/*  <div*/}
        {/*    style={{*/}
        {/*      width: selectedTab === "ARTISTS" ? "200%" : 0,*/}
        {/*      ...tabButtonStyles*/}
        {/*    }}*/}
        {/*  />*/}
        {/*</div>*/}
        {/*<div onClick={() => setSelectedTab("ALBUMS")}>*/}
        {/*  <FontAwesomeIcon icon={faRecordVinyl} />*/}
        {/*  <div*/}
        {/*    style={{*/}
        {/*      width: selectedTab === "ALBUMS" ? "200%" : 0,*/}
        {/*      ...tabButtonStyles*/}
        {/*    }}*/}
        {/*  />*/}
        {/*</div>*/}
      </div>
      <div
        style={{
          margin: "2vh 10vw 5vh 10vw",
          height: "76vh",
          overflow: "hidden"
        }}
      >
        <Playlist />
        {/*<Search />*/}
        {/*<Artists />*/}
        {/*<Albums />*/}
        {/*<div*/}
        {/*  style={{*/}
        {/*    position: "absolute",*/}
        {/*    bottom: "10px",*/}
        {/*    right: "200px",*/}
        {/*    fontSize: "64px"*/}
        {/*  }}*/}
        {/*  onClick={shuffleSongs}*/}
        {/*>*/}
        {/*  TOUCH ME*/}
        {/*</div>*/}
      </div>
    </div>
  );
};

export default connector(Tabs);
