import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBars,
  faEllipsisH,
  faRecordVinyl,
  faUsers
} from "@fortawesome/free-solid-svg-icons";

const Menu = () => {
  const [isOpen, setIsOpen] = useState(true);
  const [selected, setSelected] = useState("playlist");
  return (
    <div
      style={{
        position: "absolute",
        bottom: isOpen ? 0 : "-90vh",
        transition: "all 0.5s ease"
      }}
    >
      <div
        style={{
          textAlign: "center",
          fontSize: "72px",
          position: "relative",
          left: "50%",
          transform: "translateX(-50%)",
          height: "72px",
          width: "128px",
          backgroundColor: "rgba(255, 255, 255, .5)",
          borderRadius: "24px 24px 0 0",
          backdropFilter: "blur(20px)"
        }}
        onClick={() => setIsOpen(!isOpen)}
      >
        <FontAwesomeIcon icon={faEllipsisH} />
      </div>
      <div
        style={{
          height: "90vh",
          width: "100vw",
          backgroundColor: "rgba(255, 255, 255, .5)",
          backdropFilter: "blur(20px)",
          position: "relative",
          bottom: 0
        }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-around",
            fontSize: "72px",
            width: "90vw",
            padding: "2vh 5vw",
            textAlign: "center"
          }}
        >
          <div onClick={() => setSelected("playlist")}>
            <FontAwesomeIcon icon={faBars} />
            <div
              style={{
                width: selected === "playlist" ? "200%" : 0,
                height: "8px",
                backgroundColor: "black",
                position: "relative",
                left: "-50%",
                marginTop: "8px",
                transition: "all 0.5s ease"
              }}
            />
          </div>
          <div onClick={() => setSelected("artists")}>
            <FontAwesomeIcon icon={faUsers} />
            <div
              style={{
                width: selected === "artists" ? "200%" : 0,
                height: "8px",
                backgroundColor: "black",
                position: "relative",
                left: "-50%",
                marginTop: "8px",
                transition: "all 0.5s ease"
              }}
            />
          </div>
          <div onClick={() => setSelected("albums")}>
            <FontAwesomeIcon icon={faRecordVinyl} />
            <div
              style={{
                width: selected === "albums" ? "200%" : 0,
                height: "8px",
                backgroundColor: "black",
                position: "relative",
                left: "-50%",
                marginTop: "8px",
                transition: "all 0.5s ease"
              }}
            />
          </div>
        </div>
        <div style={{ margin: "5vh 5vw 5vh 5vw", height: "72vh" }}>Hello</div>
      </div>
    </div>
  );
};

export default Menu;
