import React, { ReactElement } from "react";
import { connect, ConnectedProps } from "react-redux";
// @ts-ignore TODO: create type file for this library
import Loader from "react-loader-spinner";
import { State } from "../store/types";

import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";

const mapStateToProps = (state: State) => ({
  areAlbumsLoading: state.albums.loading,
  areArtistsLoading: state.artists.loading
});

const connector = connect(mapStateToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const PageLoader: React.FC<PropsFromRedux> = ({
  areAlbumsLoading,
  areArtistsLoading
}): ReactElement => {
  const isLoading = areArtistsLoading && areAlbumsLoading;

  return (
    <>
      {isLoading && (
        <div
          style={{
            position: "absolute",
            transform: "translate(-50%, -50%)",
            top: "50%",
            left: "50%",
            color: "#ffb3ba",
            textAlign: "center",
            fontSize: "36px",
            fontFamily: "'Work Sans', sans-serif"
          }}
        >
          <Loader type="Bars" color="#ffb3ba" height={128} width={128} />
          <br />
          <div>Loading...</div>
        </div>
      )}
    </>
  );
};

export default connector(PageLoader);
