import React, { createRef, FC, ReactElement, useEffect, useState } from "react";
import { PlaylistState, State, Tabs } from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { setSelectedTab } from "../../store/actions";
import { updatePlaylist } from "../../store/actions/playlistActions";
import SongsTab from "./SongsTab";
import { filterData } from "../../utils";
import isempty from "lodash.isempty";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  updatePlaylist: ({ previous, current, next }: PlaylistState) =>
    dispatch(updatePlaylist({ previous, current, next })),
  setSelectedTab: (tab: Tabs) => dispatch(setSelectedTab(tab))
});

const mapStateToProps = (state: State) => ({
  artists: state.artists.data,
  albums: state.albums.data,
  playlists: state.playlists.data
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const SearchTab: FC<PropsFromRedux> = ({
  artists,
  setSelectedTab,
  updatePlaylist,
  albums,
  playlists
}): ReactElement => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredArtists, setFilteredArtists] = useState(artists);
  const [filteredAlbums, setFilteredAlbums] = useState(albums);
  const [filteredPlaylists, setFilteredPlaylists] = useState(playlists);
  const textInput = createRef<HTMLInputElement>();

  useEffect(() => {
    if (searchTerm !== "") {
      let artistsFiltered = filterData(artists, searchTerm);
      if (!isempty(artistsFiltered)) setFilteredArtists(artistsFiltered);

      let albumsFiltered = filterData(albums, searchTerm);
      if (!isempty(albumsFiltered)) setFilteredAlbums(albumsFiltered);

      let playlistsFiltered = filterData(playlists, searchTerm);
      if (!isempty(playlistsFiltered)) setFilteredAlbums(playlistsFiltered);
    }
  }, [searchTerm]);

  const handleClick = () => {
    if (textInput.current && textInput.current.value !== searchTerm) {
      setSearchTerm(textInput.current.value);
    }
  };

  return (
    <>
      <div
        style={{
          display: "flex",
          height: "4vh",
          fontSize: "36px",
          marginBottom: "20px"
        }}
      >
        <input
          ref={textInput}
          type="text"
          placeholder="Type to search..."
          style={{
            width: "calc(100% - 8vh)",
            fontSize: "36px",
            paddingLeft: "2vw"
          }}
        />
        <input
          type="button"
          style={{
            width: "8vh",
            fontSize: "36px"
          }}
          value="Search"
          onClick={() => handleClick()}
        />
      </div>
      {searchTerm === "" ? (
        <div style={{ fontSize: "48px", textAlign: "center" }}>
          Please type something in the search bar to begin.
        </div>
      ) : (
        <SongsTab
          artists={filteredArtists}
          albums={filteredAlbums}
          playlists={filteredPlaylists}
        />
      )}
    </>
  );
};

export default connector(SearchTab);
