import axios from "axios";
import { ActionCreator, Dispatch } from "redux";
import {
  FETCH_SONG,
  FETCH_SONG_FAILURE,
  FETCH_SONG_SUCCESS, FetchSong,
  FetchSongAction,
  FetchSongFailureAction,
  FetchSongSuccessAction,
  State
} from "../types";

const fetchSongAction: ActionCreator<FetchSongAction> = () => ({
  type: FETCH_SONG
});

const fetchSongFailure: ActionCreator<FetchSongFailureAction> = (error) => ({
  type: FETCH_SONG_FAILURE,
  payload: error
});

const fetchSongSuccess: ActionCreator<FetchSongSuccessAction> = (data) => ({
  type: FETCH_SONG_SUCCESS,
  payload: data
});

export const fetchSong = ({ id = "" }: FetchSong) => {
  return (dispatch: Dispatch, getState: () => State) => {
    dispatch(fetchSongAction());

    const currentArt = getState().currentSong.albumArt;

    axios
      .get("/api/song/" + id)
      .then((res) => {
        dispatch(fetchSongSuccess(res.data));
      })
      .catch((err) => {
        dispatch(fetchSongFailure(err.message));
      });
  };
};
