import get from "axios";
import { ActionCreator, Dispatch } from "redux";
import {
  FETCH_SONGS,
  FETCH_SONGS_FAILURE,
  FETCH_SONGS_SUCCESS,
  FetchSongsAction,
  FetchSongsFailureAction,
  FetchSongsSuccessAction,
  PlaylistActions,
  SET_PLAYLIST
} from "../types";
import { getBackground, setCurrentSong } from "./";

const SetPlaylistAction: ActionCreator<PlaylistActions> = (songs) => {
  const firstSong = songs[0];
  const nextSongs = songs.shift();

  return {
    type: SET_PLAYLIST,
    payload: { firstSong, nextSongs }
  };
};

const fetchSongsAction: ActionCreator<FetchSongsAction> = () => ({
  type: FETCH_SONGS
});

const fetchSongsFailure: ActionCreator<FetchSongsFailureAction> = (error) => ({
  type: FETCH_SONGS_FAILURE,
  payload: { error }
});

const fetchSongsSuccess: ActionCreator<FetchSongsSuccessAction> = (
  playlist
) => ({
  type: FETCH_SONGS_SUCCESS,
  payload: playlist
});

export const fetchSongs = ({
  id,
  type
}: {
  id: string;
  type: "album" | "artist";
}) => {
  return (dispatch: Dispatch) => {
    dispatch(fetchSongsAction());

    get(`/api/songs/${type}/${id}`)
      .then((res) => {
        dispatch(fetchSongsSuccess(res.data));
        dispatch(setCurrentSong(res.data[0]));

        // @ts-ignore
        dispatch(getBackground({ id: res.data[0].albums[0]._id }));
      })
      .catch((err) => {
        dispatch(fetchSongsFailure(err.message));
      });
  };
};
