import React, { ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlay,
  faStepBackward,
  faStepForward
} from "@fortawesome/free-solid-svg-icons";
import { Action } from "redux";
import { fetchPlaylist } from "../store/actions";
import { connect, ConnectedProps } from "react-redux";
import { State } from "../store/types";
import { ThunkDispatch } from "redux-thunk";
import { fetchSong } from "../store/actions/currentSongActions";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  fetchPlaylist: () => dispatch(fetchPlaylist()),
  fetchSong: (id: string) => dispatch(fetchSong({ id }))
});

const mapStateToProps = (state: State) => ({
  currentSong: state.currentSong.song
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const MusicPlayer: React.FC<PropsFromRedux> = ({
  currentSong,
  fetchPlaylist,
  fetchSong
}): ReactElement => {
  fetchPlaylist();
  fetchSong("5f1422054ee369215e056255");

  if (currentSong) {
    const audio = document.getElementById("audioEl");
    audio.setAttribute("src", currentSong);
  }

  return (
    <div
      style={{
        display: "block",
        margin: 0,
        position: "absolute",
        backgroundColor: "black",
        height: "100%",
        width: "100%",
        color: "white",
        fontFamily: "'Work Sans', sans-serif",
        fontSize: "18px"
      }}
    >
      <audio id="audioEl" />
      <div
        style={{
          height: "5%",
          width: "90%",
          backgroundColor: "#333",
          textAlign: "center",
          padding: "5% 5% 2% 5%"
        }}
      >
        <div style={{ fontSize: "24px" }}>NOW PLAYING</div>
        <div
          style={{
            fontSize: "48px",
            whiteSpace: "nowrap",
            overflow: "hidden",
            textOverflow: "ellipsis"
          }}
        >
          Benjamin Something - Something, something, something a new start
        </div>
      </div>
      <div
        style={{
          height: "10%",
          width: "90%",
          backgroundColor: "#333",
          position: "absolute",
          bottom: 0,
          padding: "4% 5%"
        }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <div style={{ fontSize: "48px" }}>
            <FontAwesomeIcon icon={faStepBackward} />
          </div>
          <div style={{ fontSize: "96px", margin: "0 96px" }}>
            <FontAwesomeIcon icon={faPlay} />
          </div>
          <div style={{ fontSize: "48px" }}>
            <FontAwesomeIcon icon={faStepForward} />
          </div>
        </div>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            marginTop: "48px",
            fontSize: "32px"
          }}
        >
          <div>00:00</div>
          <input type="range" style={{ width: "100%", margin: "0 24px" }} />
          <div>02:40</div>
        </div>
      </div>
    </div>
  );
};

export default connector(MusicPlayer);
