import React, { FC, ReactElement, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBars,
  faEllipsisH,
  faRecordVinyl,
  faUsers
} from "@fortawesome/free-solid-svg-icons";
import { InfoTile } from "../components/Menu";
import { State } from "../store/types";
import { connect, ConnectedProps } from "react-redux";
import { artistsToString } from "../utils";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { changeSong, fetchPlaylist } from "../store/actions";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  fetchSongs: (id: string, type: "album" | "artist") =>
    dispatch(fetchPlaylist({ id, type })),
  changeSong: (id: string) => dispatch(changeSong({ id }))
});

const mapStateToProps = (state: State) => ({
  isDark: state.currentSong.background?.isDark,
  playlists: state.playlist.data,
  artists: state.artists.data,
  albums: state.albums.data
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Menu: FC<PropsFromRedux> = ({
  isDark,
  playlists,
  albums,
  artists,
  fetchSongs,
  changeSong
}): ReactElement => {
  const [isOpen, setIsOpen] = useState(false);
  const [selected, setSelected] = useState("playlist");
  return (
    <div
      style={{
        position: "absolute",
        bottom: isOpen ? 0 : "-90vh",
        transition: "all 0.5s ease"
      }}
    >
      <div
        style={{
          textAlign: "center",
          fontSize: "72px",
          position: "relative",
          left: "50%",
          transform: "translateX(-50%)",
          height: "72px",
          width: "128px",
          backgroundColor: "rgba(255, 255, 255, .5)",
          borderRadius: "24px 24px 0 0",
          backdropFilter: "blur(20px)"
        }}
        onClick={() => setIsOpen(!isOpen)}
      >
        <FontAwesomeIcon icon={faEllipsisH} />
      </div>
      <div
        style={{
          height: "90vh",
          width: "100vw",
          backgroundColor: "rgba(255, 255, 255, .5)",
          backdropFilter: "blur(20px)",
          position: "relative",
          bottom: 0
        }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-around",
            fontSize: "72px",
            width: "90vw",
            height: "10vh",
            margin: "0vh 5vw 1vh 5vw",
            textAlign: "center"
          }}
        >
          <div onClick={() => setSelected("playlist")}>
            <FontAwesomeIcon icon={faBars} />
            <div
              style={{
                width: selected === "playlist" ? "200%" : 0,
                height: "8px",
                backgroundColor: "black",
                position: "relative",
                left: "-50%",
                marginTop: "8px",
                transition: "all 0.5s ease"
              }}
            />
          </div>
          <div onClick={() => setSelected("artists")}>
            <FontAwesomeIcon icon={faUsers} />
            <div
              style={{
                width: selected === "artists" ? "200%" : 0,
                height: "8px",
                backgroundColor: "black",
                position: "relative",
                left: "-50%",
                marginTop: "8px",
                transition: "all 0.5s ease"
              }}
            />
          </div>
          <div onClick={() => setSelected("albums")}>
            <FontAwesomeIcon icon={faRecordVinyl} />
            <div
              style={{
                width: selected === "albums" ? "200%" : 0,
                height: "8px",
                backgroundColor: "black",
                position: "relative",
                left: "-50%",
                marginTop: "8px",
                transition: "all 0.5s ease"
              }}
            />
          </div>
        </div>
        <div style={{ margin: "2vh 10vw", height: "76vh" }}>
          {selected === "playlist" && playlists && playlists.length > 0
            ? playlists.map((playlist) => (
                <InfoTile
                  key={`info-tile-${playlist._id}`}
                  title={playlist.name}
                  subtitle={artistsToString(playlist.artists)}
                  onClick={() => {
                    changeSong(playlist._id);
                    setIsOpen(false);
                  }}
                />
              ))
            : selected === "playlist" && (
                <div style={{ fontSize: "48px", textAlign: "center" }}>
                  There are no song playlists currently selected.
                </div>
              )}
          {selected === "albums" && albums && albums.length > 0
            ? albums.map((album) => (
                <InfoTile
                  key={`info-tile-${album._id}`}
                  title={album.name}
                  onClick={() => {
                    fetchSongs(album._id, "album");
                    setSelected("playlist");
                  }}
                />
              ))
            : selected === "albums" && (
                <div style={{ fontSize: "48px", textAlign: "center" }}>
                  Sorry no albums have been loaded.
                </div>
              )}
          {selected === "artists" && artists && artists.length > 0
            ? artists.map((artist) => (
                <InfoTile
                  key={`info-tile-${artist._id}`}
                  title={artist.name}
                  onClick={() => {
                    fetchSongs(artist._id, "artist");
                    setSelected("playlist");
                  }}
                />
              ))
            : selected === "artists" && (
                <div style={{ fontSize: "48px", textAlign: "center" }}>
                  Sorry no artists have been loaded.
                </div>
              )}
        </div>
      </div>
    </div>
  );
};

export default connector(Menu);
