import React, { ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faCoffee, faPlay, faStepBackward, faStepForward} from "@fortawesome/free-solid-svg-icons";
// import { Controls } from "../components/musicPlayer";

const MusicPlayer: React.FC = (): ReactElement => (
  <div
    style={{
      display: "block",
      margin: 0,
      position: "absolute",
      backgroundColor: "black",
      height: "100%",
      width: "100%",
      color: "white",
      fontFamily: "'Work Sans', sans-serif",
      fontSize: "18px"
    }}
  >
    <div
      style={{
        height: "5%",
        width: "90%",
        backgroundColor: "#333",
        textAlign: "center",
        padding: "5% 5% 2% 5%"
      }}
    >
      <div style={{ fontSize: "24px" }}>NOW PLAYING</div>
      <div
        style={{
          fontSize: "48px",
          whiteSpace: "nowrap",
          overflow: "hidden",
          textOverflow: "ellipsis"
        }}
      >
        Benjamin Something - Something, something, something a new start
      </div>
    </div>

    <div
      style={{
        height: "10%",
        width: "100%",
        backgroundColor: "#333",
        position: "absolute",
        bottom: 0
      }}
    >
      <FontAwesomeIcon icon={faStepBackward} />
      <FontAwesomeIcon icon={faPlay} />
      <FontAwesomeIcon icon={faStepForward} />
    </div>
  </div>
);

export default MusicPlayer;
