import {
  FETCH_PLAYLIST,
  FETCH_PLAYLIST_FAILURE, FETCH_PLAYLIST_STARTED,
  FETCH_PLAYLIST_SUCCESS,
  PlaylistActions,
  Song,
  UPDATE_PLAYLIST
} from "../types";

interface PlaylistState {
  error: any;
  loading: boolean;
  data: Song[] | [];
}

const initialState: PlaylistState = {
  loading: false,
  data: [],
  error: null
};

export const playlist = (
  state: PlaylistState = initialState,
  action: PlaylistActions
) => {
  switch (action.type) {
    case FETCH_PLAYLIST_STARTED:
      return {
        ...state,
        loading: true
      };
    case FETCH_PLAYLIST_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload.error
      };
    case FETCH_PLAYLIST_SUCCESS:
      return {
        ...state,
        loading: false,
        error: null,
        data: action.payload
      };
    case UPDATE_PLAYLIST:
      return {
        ...state,
        data: action.payload.data
      };
    default: {
      return state;
    }
  }
};
