import React, { FC, ReactElement } from "react";
import { InfoTile } from "./";
import { Song, State } from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { artistsToString } from "../../utils";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { changeSong, setIsOpen } from "../../store/actions";
const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  changeSong: (song: Song, albumId: string) =>
    dispatch(changeSong(song, albumId))
});

const mapStateToProps = (state: State) => ({
  current: state.playlist.current,
  next: state.playlist.next,
  previous: state.playlist.previous,
  selectedTab: state.menu.selectedTab
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Playlist: FC<PropsFromRedux> = ({
  current,
  next,
  previous,
  selectedTab,
  changeSong
}): ReactElement => {
  const handleClick = (id: string) => {
    if (current) {
      changeSong(current, id);
      setIsOpen(false);
    }
  };
  return (
    <>
      {selectedTab === "PLAYLIST" &&
        previous &&
        previous.length > 0 &&
        previous.map((playlist) => (
          <InfoTile
            key={`info-tile-${playlist._id}`}
            title={playlist.name}
            subtitle={artistsToString(playlist.artists)}
            onClick={() => handleClick(playlist._id)}
          />
        ))}
      {selectedTab === "PLAYLIST" && current && (
        <InfoTile
          key={`info-tile-${current._id}`}
          title={current.name}
          subtitle={artistsToString(current.artists)}
          onClick={() => handleClick(current._id)}
        />
      )}
      {selectedTab === "PLAYLIST" &&
        next &&
        next.length > 0 &&
        next.map((playlist) => (
          <InfoTile
            key={`info-tile-${playlist._id}`}
            title={playlist.name}
            subtitle={artistsToString(playlist.artists)}
            onClick={() => handleClick(playlist._id)}
          />
        ))}
    </>
  );
};

export default connector(Playlist);
