import React, { ReactElement } from "react";
import { connect, ConnectedProps } from "react-redux";
import { State } from "../../store/types";
import { artistsToString } from "../../utils";

const mapStateToProps = (state: State) => ({
  artists: state.currentSong.data?.artists,
  songName: state.currentSong.data?.name
});

const connector = connect(mapStateToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const NowPlaying: React.FC<PropsFromRedux> = ({
  artists = [{ name: "Unknown Artist" }],
  songName = "Unknown"
}): ReactElement => {
  let artistNames = ["Unknown Artist"];

  if (artists.length > 0) {
    // @ts-ignore error with typescript being unable to recognise union types correctly
    artistNames = artists.reduce(
      (acc: string[], { name }: { name: string }) => {
        acc.push(name);
        return acc;
      },
      []
    );
  }

  return (
    <div
      style={{
        height: "5%",
        width: "90%",
        textAlign: "center",
        padding: "5% 5% 2% 5%"
      }}
    >
      <div style={{ fontSize: "24px" }}>NOW PLAYING</div>
      <div
        style={{
          fontSize: "48px",
          whiteSpace: "nowrap",
          overflow: "hidden",
          textOverflow: "ellipsis"
        }}
      >
        {songName} - {artistsToString(artistNames)}
      </div>
    </div>
  );
};

export default connector(NowPlaying);
