import { CHANGE_SONG, TOGGLE_PLAYING } from "./types";
import { MUSIC_REGISTRY } from "../musicRegistry";

export const changeSong = (newSong: MUSIC_REGISTRY) => ({
  type: CHANGE_SONG,
  payload: newSong
});

export const togglePlaying = () => ({
  type: TOGGLE_PLAYING
});
