import React, { CSSProperties, ReactElement, useState } from "react";
import { connect, ConnectedProps } from "react-redux";
import { artistsToString, playlistsToArray } from "../../utils";
import { State } from "../../store/types";
import { InfoTile } from "./index";

const buttonStyles: CSSProperties = {
  padding: "30px 0",
  textAlign: "center"
};

const mapStateToProps = (state: State) => ({
  artists: state.playlist.current?.artists,
  songName: state.playlist.current?.name,
  data: state.playlist.current,
  playlists: state.playlists.data
});

const connector = connect(mapStateToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const SongMenu: React.FC<PropsFromRedux> = ({
  artists = [{ name: "Unknown Artist" }],
  songName = "Unknown",
  data,
  playlists
}): ReactElement => {
  const [playlistView, setPlaylistView] = useState(true);
  const playlistInfo = playlists ? playlistsToArray(playlists) : [];

  const playlistHandler = () => {
    setPlaylistView(true);
  };

  return (
    <div
      style={{
        position: "absolute",
        width: "100%",
        height: "100%",
        backgroundColor: "rgba(255, 255, 255, .5)",
        fontSize: "48px",
        zIndex: 2,
        backdropFilter: "blur(20px)"
      }}
    >
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          borderRadius: "24px",
          backgroundColor: "white",
          padding: "10%",
          width: "60%",
          minHeight: "30%",
          maxHeight: "40%",
          overflowY: "scroll"
        }}
      >
        Creative Minds
        <div style={{ fontSize: "36px" }}>Benjamin Tissot, BenSound</div>
        <hr />
        {!playlistView ? (
          <>
            <div style={buttonStyles}>Play now</div>
            <div style={buttonStyles}>Play next</div>
            <div style={buttonStyles}>Add to end of queue</div>
            <div style={buttonStyles} onClick={playlistHandler}>
              Add to playlist
            </div>
          </>
        ) : (
          <>
            {playlistInfo.map(({ name, id }) => (
              <div>
                <input type="checkbox" name={name} value={id} id={id} style={{ height: "32px", width: "32px", marginRight: "32px"}} />
                <label htmlFor={id}>{name}</label>
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};

export default connector(SongMenu);
