import { ActionCreator } from "redux";
import {
  CHANGE_SONG,
  TOGGLE_PLAYING,
  ChangeSongAction,
  PlayingStatusAction,
  Song
} from "../types";
import { fetchPlaylist } from "./playlistActions";

export const changeSong: ActionCreator<ChangeSongAction> = (newSong: Song) => ({
  type: CHANGE_SONG,
  payload: newSong
});

export const togglePlaying: ActionCreator<PlayingStatusAction> = () => ({
  type: TOGGLE_PLAYING
});

export { fetchPlaylist }
