import React, { FC, ReactElement, useState } from "react";
import {
  ArtistsAndAlbumsData,
  PlaylistState,
  State,
  Tabs
} from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { setSelectedTab } from "../../store/actions";
import { updatePlaylist } from "../../store/actions/playlistActions";
import Accordion from "./Accordion";
import TabButton from "./TabButton";

interface SongsTabProps {
  searchFor?: string;
}

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  updatePlaylist: ({ previous, current, next }: PlaylistState) =>
    dispatch(updatePlaylist({ previous, current, next })),
  setSelectedTab: (tab: Tabs) => dispatch(setSelectedTab(tab))
});

const mapStateToProps = (state: State) => ({
  artists: state.artists.data,
  albums: state.albums.data,
  playlists: state.playlists.data
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const SongsTab: FC<PropsFromRedux & SongsTabProps> = ({
  artists,
  setSelectedTab,
  updatePlaylist,
  albums,
  playlists,
  searchFor
}): ReactElement => {
  const [selectedSongTab, setSelectedSongTab] = useState("ARTISTS");
  const handleClick = (id: string) => {
    if (artists) {
      const current = artists[id].songs[0];
      const next = artists[id].songs.slice(1, artists[id].songs.length);

      updatePlaylist({ previous: [], current, next });
      setSelectedTab("PLAYLIST");
    }
  };

  const filterData = (
    data: ArtistsAndAlbumsData | undefined,
    searchFor: string
  ) => {
    let filteredData = data ? { ...data } : data;
    if (filteredData) {
      // @ts-ignore
      Object.keys(filteredData).reduce((acc: {}, curr) => {
        let containsSearchTerm = false;
        if (
          filteredData &&
          filteredData[curr] &&
          filteredData[curr].name.includes(searchFor)
        ) {
          acc = { ...acc, [curr]: filteredData[curr] };
          return acc;
        } else if (filteredData && filteredData[curr] && filteredData[curr].songs) {
          filteredData[curr].songs.forEach(song => {

          })
        }
      }, {});
    } else {
      return filteredData;
    }
  };

  const artistsData = searchFor ? filterData(artists, searchFor) : artists;
  const albumsData = searchFor ? filterData(albums, searchFor) : albums;
  const playlistsData = searchFor
    ? filterData(playlists, searchFor)
    : playlists;

  return (
    <>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-around",
          fontSize: "36px",
          width: "100%",
          textAlign: "center",
          marginBottom: "20px"
        }}
      >
        <TabButton
          title="ARTISTS"
          onClick={() => setSelectedSongTab("ARTISTS")}
          isSelected={selectedSongTab === "ARTISTS"}
        />
        <TabButton
          title="ALBUMS"
          onClick={() => setSelectedSongTab("ALBUMS")}
          isSelected={selectedSongTab === "ALBUMS"}
        />
        <TabButton
          title="PLAYLISTS"
          onClick={() => setSelectedSongTab("PLAYLISTS")}
          isSelected={selectedSongTab === "PLAYLISTS"}
        />
      </div>
      {selectedSongTab === "ARTISTS" && artists && artists !== {}
        ? Object.keys(artists).map((id) => (
            <Accordion
              key={`accordion-${id}`}
              title={artists[id].name}
              data={artists[id].songs}
            />
          ))
        : selectedSongTab === "ARTISTS" && (
            <div style={{ fontSize: "48px", textAlign: "center" }}>
              Sorry no artists have been found.
            </div>
          )}
      {selectedSongTab === "ALBUMS" && albums && albums !== {}
        ? Object.keys(albums).map((id) => (
            <Accordion
              key={`accordion-${id}`}
              title={albums[id].name}
              data={albums[id].songs}
            />
          ))
        : selectedSongTab === "ALBUMS" && (
            <div style={{ fontSize: "48px", textAlign: "center" }}>
              Sorry no albums have been found.
            </div>
          )}
      {selectedSongTab === "PLAYLISTS" && playlists && playlists !== {}
        ? Object.keys(playlists).map((id) => (
            <Accordion
              key={`accordion-${id}`}
              title={playlists[id].name}
              data={playlists[id].songs}
            />
          ))
        : selectedSongTab === "PLAYLISTS" && (
            <div style={{ fontSize: "48px", textAlign: "center" }}>
              Sorry no playlists have been found.
            </div>
          )}
    </>
  );
};

export default connector(SongsTab);
