import React, { FC, ReactElement } from "react";
import { State } from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { setIsOpen } from "../../store/actions";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  setIsOpen: (isOpen: boolean) => dispatch(setIsOpen(isOpen))
});

const mapStateToProps = (state: State) => ({
  current: state.playlist.current,
  next: state.playlist.next,
  previous: state.playlist.previous,
  selectedTab: state.menu.selectedTab
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Search: FC<PropsFromRedux> = ({
  current,
  next,
  previous,
  selectedTab,
  setIsOpen
}): ReactElement => {
  const handleClick = (id: string) => {
    setIsOpen(false);
  };
  return (
    <>
      {selectedTab === "SEARCH" && (
        <div style={{ display: "flex", height: "4vh" }}>
          <input
            type="text"
            style={{
              width: "calc(100% - 4vh)",
              borderTopLeftRadius: "1.5vh",
              borderBottomLeftRadius: "1.5vh"
            }}
          />

          <input
            type="button"
            style={{
              width: "4vh",
              borderTopRightRadius: "1.5vh",
              borderBottomRightRadius: "1.5vh"
            }}
          >
            hello
            {/*<FontAwesomeIcon icon={faSearch} />*/}
          </input>
        </div>
      )}
    </>
  );
};

export default connector(Search);
