import React, { FC, ReactElement, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronDown,
  faChevronUp,
  faPlay
} from "@fortawesome/free-solid-svg-icons";
import { Song } from "../../store/types";
import { InfoTile } from "./index";

interface AccordionProps {
  title: string;
  data: Song[];
}

const Accordion: FC<AccordionProps> = ({ title, data }): ReactElement => {
  const [isOpen, setIsOpen] = useState(true);

  console.log(data);

  return (
    <>
      <div
        style={{
          width: "100%",
          display: "flex",
          alignItems: "center",
          fontSize: "48px",
          padding: "2vh 0"
        }}
        onClick={() => setIsOpen(!isOpen)}
      >
        <div style={{ width: "calc(100% - 42px)" }}>{title}</div>
        <div style={{ width: "42px" }}>
          <FontAwesomeIcon icon={isOpen ? faChevronUp : faChevronDown} />
        </div>
      </div>

      {isOpen && (
        <div style={{ display: "block" }}>
          <div style={{ fontSize: "48px" }}>
            <FontAwesomeIcon
              icon={faPlay}
              style={{ marginLeft: "40px", marginRight: "40px" }}
            />
            Play all
          </div>
          {data.map((song) => (
            <InfoTile
              key={`info-tile-${song._id}`}
              title={song.name}
              onClick={() => console.log(song._id)}
            />
          ))}
        </div>
      )}
    </>
  );
};

export default Accordion;
