import {
  CurrentSongActions,
  CurrentSongState,
  FETCH_SONG,
  FETCH_SONG_FAILURE,
  FETCH_SONG_SUCCESS
} from "../types";

const initialState = {
  isPlaying: false,
  data: undefined
};

export const currentSong = (
  state: CurrentSongState = initialState,
  action: CurrentSongActions
) => {
  switch (action.type) {
    case FETCH_SONG:
      return {
        ...state,
        data
      };
    default: {
      return state;
    }
  }
};
