import React, { FC, ReactElement } from "react";
import { Action } from "redux";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { faBars, faMusic, faSearch } from "@fortawesome/free-solid-svg-icons";
import { Playlist, TabButton, SearchTab, SongsTab } from "./";
import { setSelectedTab, shuffleSongs } from "../../store/actions";
import { State, Tabs as TabsType } from "../../store/types";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  setSelectedTab: (tab: TabsType) => dispatch(setSelectedTab(tab)),
  shuffleSongs: () => dispatch(shuffleSongs())
});

const mapStateToProps = (state: State) => ({
  selectedTab: state.menu.selectedTab,
  artists: state.artists.data,
  albums: state.albums.data,
  playlists: state.playlists.data
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Tabs: FC<PropsFromRedux> = ({
  shuffleSongs,
  selectedTab,
  setSelectedTab,
  artists,
  albums,
  playlists
}): ReactElement => {
  const tabsData = [
    {
      type: "SEARCH" as TabsType,
      icon: faSearch
    },
    {
      type: "PLAYLIST" as TabsType,
      icon: faBars
    },
    {
      type: "SONGS" as TabsType,
      icon: faMusic
    }
  ];

  return (
    <div
      style={{
        height: "90vh",
        width: "100vw",
        backgroundColor: "rgba(255, 255, 255, .5)",
        backdropFilter: "blur(20px)",
        position: "relative",
        bottom: 0
      }}
    >
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-around",
          fontSize: "72px",
          width: "90vw",
          height: "8vh",
          margin: "0 5vw",
          textAlign: "center"
        }}
      >
        {tabsData.map(({ icon, type }) => (
          <TabButton
            key={`tab-button-${type}`}
            icon={icon}
            isSelected={selectedTab === type}
            onClick={() => setSelectedTab(type)}
          />
        ))}
      </div>
      <div
        style={{
          margin: "0vh 10vw 5vh 10vw",
          height: "77vh",
          overflow: "hidden",
          overflowY: "scroll"
        }}
      >
        {selectedTab === "PLAYLIST" && <Playlist />}
        {selectedTab === "SONGS" && (
          <SongsTab artists={artists} albums={albums} playlists={playlists} />
        )}
        {selectedTab === "SEARCH" && <SearchTab />}

        {/*<div*/}
        {/*  style={{*/}
        {/*    position: "absolute",*/}
        {/*    bottom: "10px",*/}
        {/*    right: "200px",*/}
        {/*    fontSize: "64px"*/}
        {/*  }}*/}
        {/*  onClick={shuffleSongs}*/}
        {/*>*/}
        {/*  TOUCH ME*/}
        {/*</div>*/}
      </div>
    </div>
  );
};

export default connector(Tabs);
