import axios from "axios";
import { ActionCreator, Dispatch } from "redux";
import {
  FETCH_PLAYLIST,
  FETCH_PLAYLIST_FAILURE,
  FETCH_PLAYLIST_SUCCESS,
  FetchPlaylistAction,
  FetchPlaylistFailureAction,
  FetchPlaylistSuccessAction
} from "../types";
// import {setCurrentSong} from "./currentSongActions";

const fetchPlaylistAction: ActionCreator<FetchPlaylistAction> = () => ({
  type: FETCH_PLAYLIST
});

const fetchPlaylistFailure: ActionCreator<FetchPlaylistFailureAction> = (
  error
) => ({
  type: FETCH_PLAYLIST_FAILURE,
  payload: { error }
});

const fetchPlaylistSuccess: ActionCreator<FetchPlaylistSuccessAction> = (
  playlist
) => ({
  type: FETCH_PLAYLIST_SUCCESS,
  payload: {
    ...playlist
  }
});

export const fetchPlaylist = ({
  id,
  type
}: {
  id: string;
  type: "album" | "artist";
}) => {
  return (dispatch: Dispatch) => {
    // dispatch(fetchPlaylistAction());

    axios
      .get(`/api/songs/${type}/${id}`)
      .then((res) => {
        dispatch(fetchPlaylistSuccess(res.data));
        console.log("hi")
      })
      .catch((err) => {
        dispatch(fetchPlaylistFailure(err.message));
      });
  };
};
