import React, { ReactElement } from "react";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
// @ts-ignore TODO: create type file for this library
import { Menu, MusicPlayer } from "./views";
import { State } from "./store/types";
import { fetchAlbums, fetchArtists } from "./store/actions";
import PageLoader from "./views/PageLoader";

const mapStateToProps = (state: State) => ({
  backgroundColor: state.currentSong.background?.colour
});

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  fetchAlbums: () => dispatch(fetchAlbums()),
  fetchArtists: () => dispatch(fetchArtists())
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const App: React.FC<PropsFromRedux> = ({
  backgroundColor = "#cdcdcd",
  fetchAlbums,
  fetchArtists
}): ReactElement => {
  fetchAlbums();
  fetchArtists();

  return (
    <>
      <div
        style={{
          backgroundColor,
          width: "100vw",
          height: "100vh",
          fontFamily: "'Work Sans', sans-serif",
          overflow: "hidden"
        }}
      >
        <PageLoader />
        <MusicPlayer />
        <Menu />
      </div>
    </>
  );
};

export default connector(App);
