import React, { FC, ReactElement } from "react";

interface InfoTile {
  title: string;
  subtitle?: string;
  onClick?: () => void;
}

const InfoTile: FC<InfoTile> = ({ title, subtitle, onClick }): ReactElement => (
  <div style={{ width: "100%", height: "4vh", display: "flex" }} onClick={onClick}>
    <div style={{ fontSize: "48px" }}>{title}</div>
    {subtitle && <div style={{ fontSize: "36px" }}>{subtitle}</div>}
  </div>
);

export default InfoTile;
