import React, {FC, ReactElement, useState} from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronCircleRight } from "@fortawesome/free-solid-svg-icons";
import {SongMenu} from "./index";

interface InfoTile {
  selected?: boolean;
  title: string;
  subtitle?: string;
  onClick?: () => void;
}

const InfoTile: FC<InfoTile> = ({
  title,
  subtitle,
  onClick,
  selected = false
}): ReactElement => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  return (
    <>
    <div
      style={{
        width: "100%",
        display: "flex",
        alignItems: "center",
        fontSize: "48px",
        padding: "2vh 0"
      }}
      onClick={onClick}
    >
      <div style={{ width: "57px", height: "auto", paddingRight: "32px" }}>
        {selected && <FontAwesomeIcon icon={faChevronCircleRight} />}
      </div>
      <div>
        <div style={{ fontSize: "48px" }}>{title}</div>
        {subtitle && <div style={{ fontSize: "36px" }}>{subtitle}</div>}
      </div>
    </div>
      {isMenuOpen && <SongMenu songName={title} artists={subtitle} />}
      </>
  );
};

export default InfoTile;
