import { BasicDetails } from "../store/types";

export const artistsToString = (artists: BasicDetails[]): string =>
  artists
    .reduce((acc, { name }) => {
      // @ts-ignore
      acc.push(name);
      return acc;
    }, [])
    .join(", ");
