import {
  CurrentSongActions,
  CurrentSongState,
  NEXT_SONG,
  PREV_SONG,
  TOGGLE_PLAYING
} from "../types";

const initialState = {
  isPlaying: false,
  song: null,
  data: {}
};

export const currentSong = (
  state: CurrentSongState = initialState,
  action: CurrentSongActions
) => {
  switch (action.type) {
    case NEXT_SONG:
      return state;
    case PREV_SONG:
      return state;
    case TOGGLE_PLAYING:
      return {
        ...state,
        isPlaying: !state.isPlaying
      };
    default: {
      return state;
    }
  }
};

export {};
