import { MUSIC_REGISTRY } from "../musicRegistry";

export const TOGGLE_PLAYING = "TOGGLE_PLAYING";
export const CHANGE_SONG = "CHANGE_SONG";

export interface State {
  currentlyPlaying: MUSIC_REGISTRY;
  playingStatus: boolean;
}

export interface PlayingStatusAction {
  type: typeof TOGGLE_PLAYING;
}

export interface ChangeSongAction {
  type: typeof CHANGE_SONG;
  payload: MUSIC_REGISTRY;
}
