import React, { ReactElement } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPause,
  faPlay,
  faStepBackward,
  faStepForward
} from "@fortawesome/free-solid-svg-icons";
import { connect, ConnectedProps } from "react-redux";
import { State } from "../store/types";
import {AlbumArt} from "./index";
import ReactAudioPlayer from "react-audio-player";

const mapStateToProps = (state: State) => ({
  isPlaying: state.currentSong.isPlaying
});

const connector = connect(mapStateToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Controls: React.FC<PropsFromRedux> = ({ isPlaying }): ReactElement => {
  let audioPlayer: ReactAudioPlayer;

  return (
    <div
      style={{
        height: "10%",
        width: "90%",
        position: "absolute",
        bottom: 0,
        padding: "4% 5%"
      }}
    >
      <ReactAudioPlayer
        ref={(element: ReactAudioPlayer) => (audioPlayer = element)}
        // src={currentSong.data ? `/api/song/${currentSong.data._id}` : ""}
        listenInterval={1000}
        onListen={(e) => console.log(e)}
        onLoadedMetadata={(data) => console.log(data)}
      />
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <div style={{ fontSize: "48px" }}>
          <FontAwesomeIcon icon={faStepBackward} />
        </div>
        <div style={{ fontSize: "96px", margin: "0 96px" }}>
          <FontAwesomeIcon icon={isPlaying ? faPlay : faPause} />
        </div>
        <div style={{ fontSize: "48px" }}>
          <FontAwesomeIcon icon={faStepForward} />
        </div>
      </div>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginTop: "48px",
          fontSize: "32px"
        }}
      >
        <div>00:00</div>
        <input type="range" style={{ width: "100%", margin: "0 24px" }} />
        <div>02:40</div>
      </div>
    </div>
  );
};

export default connector(Controls);
