import React, { FC, ReactElement, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronDown,
  faChevronUp,
  faPlay
} from "@fortawesome/free-solid-svg-icons";
import { PlaylistState, Song, State, Tabs } from "../../store/types";
import { InfoTile } from "./index";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { updatePlaylist } from "../../store/actions/playlistActions";
import { setSelectedTab } from "../../store/actions";
import { connect, ConnectedProps } from "react-redux";

interface AccordionProps {
  title: string;
  data: Song[];
  filterData: (data: Song[]) => Song[];
}

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  updatePlaylist: ({ previous, current, next }: PlaylistState) =>
    dispatch(updatePlaylist({ previous, current, next })),
  setSelectedTab: (tab: Tabs) => dispatch(setSelectedTab(tab))
});

const connector = connect(null, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Accordion: FC<PropsFromRedux & AccordionProps> = ({
  updatePlaylist,
  setSelectedTab,
  title,
  data,
  filterData
}): ReactElement => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    if (data) {
      const current = data[0];
      const next = data.slice(1, data.length);

      updatePlaylist({ previous: [], current, next });
      setSelectedTab("PLAYLIST");
    }
  };

  const filteredData = filterData ? filterData(data) : data;

  return (
    <>
      <div
        style={{
          width: "100%",
          display: "flex",
          alignItems: "center",
          fontSize: "48px",
          padding: "1.5vh 0"
        }}
        onClick={() => setIsOpen(!isOpen)}
      >
        <div style={{ width: "calc(100% - 42px)" }}>{title}</div>
        <div style={{ width: "42px" }}>
          <FontAwesomeIcon icon={isOpen ? faChevronUp : faChevronDown} />
        </div>
      </div>

      {isOpen && (
        <div style={{ display: "block" }}>
          <div style={{ fontSize: "48px" }} onClick={handleClick}>
            <FontAwesomeIcon
              icon={faPlay}
              style={{ marginLeft: "8px", marginRight: "40px" }}
            />
            Play all
          </div>
          {data.map((song) => (
            <InfoTile
              key={`info-tile-${song._id}`}
              title={song.name}
              onClick={() => console.log(song._id)}
            />
          ))}
        </div>
      )}
    </>
  );
};

export default connector(Accordion);
