import {
  CurrentSongActions,
  CurrentSongState,
  FETCH_SONG,
  FETCH_SONG_FAILURE,
  FETCH_SONG_SUCCESS
} from "../types";

const initialState = {
  isPlaying: false,
  song: null,
  albumArt: null,
  data: {}
};

export const currentSong = (
  state: CurrentSongState = initialState,
  action: CurrentSongActions
) => {
  switch (action.type) {
    case FETCH_SONG:
      return state;
    case FETCH_SONG_FAILURE:
      return state;
    case FETCH_SONG_SUCCESS:
      return {
        ...state,
        song: action.payload
      };
    // case SET_SONG:
    //   return action;
    // case TOGGLE_PLAYING:
    //   return {
    //     ...state,
    //     isPlaying: !state.isPlaying
    //   };
    default: {
      return state;
    }
  }
};

export {};
