import { CurrentSongActions, CurrentSongState, SET_SONG } from "../types";

const initialState = {
  isPlaying: false
};

export const currentSong = (
  state: CurrentSongState = initialState,
  action: CurrentSongActions
) => {
  switch (action.type) {
    case SET_SONG:
      return {
        ...state,
        data: action.data,
        albumArt: `/api/album-art/${action.data.albums[0]._id}`,
        song: `/api/song/${action.data._id}`
      };
    default: {
      return state;
    }
  }
};
