// Constants

import { Song } from "./@types";

export const FETCH_PLAYLIST = "FETCH_PLAYLIST";
export const FETCH_PLAYLIST_FAILURE = "FETCH_PLAYLIST_FAILURE";
export const FETCH_PLAYLIST_SUCCESS = "FETCH_PLAYLIST_SUCCESS";

export const FETCH_SONG = "FETCH_SONG";
export const FETCH_SONG_FAILURE = "FETCH_SONG_FAILURE";
export const FETCH_SONG_SUCCESS = "FETCH_SONG_SUCCESS";

// Action types

export interface FetchPlaylistAction {
  type: typeof FETCH_PLAYLIST;
}

export interface FetchPlaylistFailureAction {
  type: typeof FETCH_PLAYLIST_FAILURE;
  payload: {
    error: Error;
  };
}

export interface FetchPlaylistSuccessAction {
  type: typeof FETCH_PLAYLIST_SUCCESS;
  payload: Song[];
}

export type PlaylistActions =
  | FetchPlaylistAction
  | FetchPlaylistFailureAction
  | FetchPlaylistSuccessAction;

export interface FetchSongAction {
  type: typeof FETCH_SONG;
}

export interface FetchSongFailureAction {
  type: typeof FETCH_SONG_FAILURE;
  payload: Error;
}

export interface FetchSongSuccessAction {
  type: typeof FETCH_SONG_SUCCESS;
  payload: AudioBuffer;
}

// export interface SetSongAction {
//   type: typeof SET_SONG;
// }
