import React, { FC, ReactElement } from "react";
import { InfoTile } from "./";
import { Song, State, Tabs } from "../../store/types";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import { Action } from "redux";
import { fetchSongs, setPlaylist, setSelectedTab } from "../../store/actions";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  setPlaylist: (songs: Song[]) => dispatch(setPlaylist(songs)),
  setSelectedTab: (tab: Tabs) => dispatch(setSelectedTab(tab))
});

const mapStateToProps = (state: State) => ({
  albums: state.albums.data,
  selectedTab: state.menu.selectedTab
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Albums: FC<PropsFromRedux> = ({
  albums,
  setPlaylist,
  selectedTab,
  setSelectedTab
}): ReactElement => {
  const handleClick = (id: string) => {
    if (albums) console.log(albums[id].songs.length);
    if (albums) setPlaylist(albums[id].songs);
    setSelectedTab("PLAYLIST");
  };

  return (
    <>
      {selectedTab === "ALBUMS" && albums && albums !== {}
        ? Object.keys(albums).map((id) => (
            <InfoTile
              key={`info-tile-${id}`}
              title={albums[id].name}
              onClick={() => handleClick(id)}
            />
          ))
        : selectedTab === "ALBUMS" && (
            <div style={{ fontSize: "48px", textAlign: "center" }}>
              Sorry no albums have been loaded.
            </div>
          )}
    </>
  );
};

export default connector(Albums);
