import { ActionCreator, Dispatch } from "redux";
import axios from "axios";
import {
  CHANGE_SONG,
  ChangeSongAction,
  FETCH_PLAYLIST_FAILURE,
  FETCH_PLAYLIST_STARTED,
  FETCH_PLAYLIST_SUCCESS,
  FetchPlaylistFailureAction,
  FetchPlaylistStartedAction,
  FetchPlaylistSuccessAction,
  PlayingStatusAction,
  Song,
  TOGGLE_PLAYING
} from "./types";

export const fetchPlaylist = () => {
  return (dispatch: Dispatch) => {
    dispatch(fetchPlaylistStarted());

    axios
      .get("/api/song")
      .then((res) => {
        dispatch(fetchPlaylistSuccess(res));
      })
      .catch((err) => {
        dispatch(fetchPlaylistFailure(err));
      });
  };
};

export const fetchPlaylistFailure: ActionCreator<FetchPlaylistFailureAction> = (
  error
) => ({
  type: FETCH_PLAYLIST_FAILURE,
  payload: { error }
});

export const fetchPlaylistStarted: ActionCreator<FetchPlaylistStartedAction> = () => ({
  type: FETCH_PLAYLIST_STARTED
});

export const fetchPlaylistSuccess: ActionCreator<FetchPlaylistSuccessAction> = (
  playlist
) => ({
  type: FETCH_PLAYLIST_SUCCESS,
  payload: {
    ...playlist
  }
});

export const changeSong: ActionCreator<ChangeSongAction> = (newSong: Song) => ({
  type: CHANGE_SONG,
  payload: newSong
});

export const togglePlaying: ActionCreator<PlayingStatusAction> = () => ({
  type: TOGGLE_PLAYING
});
