import React, { ReactElement } from "react";
import { connect, ConnectedProps } from "react-redux";
import FastAverageColor from "fast-average-color";
import { MusicPlayer } from "./views";
import { State } from "./store/types";

const mapStateToProps = (state: State) => ({
  albumArt: state.currentSong.albumArt
});

const connector = connect(mapStateToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const App: React.FC<PropsFromRedux> = ({ albumArt }): ReactElement => {
  let backgroundColor = "black";

  const fac = new FastAverageColor();

  if (albumArt) {
    console.log("here");
    fac
      // @ts-ignore
      .getColor(albumArt, { algorithm: "dominant" })
      .then((color: IFastAverageColorResult) => {
        backgroundColor = color.hex;
      })
      .catch((error: Error) => console.error(error));
  }
  console.log(backgroundColor);
  return (
    <div style={{ backgroundColor, width: "100vw", height: "100vh" }}>
      <MusicPlayer />
    </div>
  );
};

export default connector(App);
