// Constants

export const FETCH_PLAYLIST = "FETCH_PLAYLIST";
export const FETCH_PLAYLIST_FAILURE = "FETCH_PLAYLIST_FAILURE";
export const FETCH_PLAYLIST_STARTED = "FETCH_PLAYLIST_STARTED";
export const FETCH_PLAYLIST_SUCCESS = "FETCH_PLAYLIST_SUCCESS";
export const CHANGE_SONG = "CHANGE_SONG";
export const TOGGLE_PLAYING = "TOGGLE_PLAYING";
export const UPDATE_PLAYLIST = "UPDATE_PLAYLIST";

// Typescript types

export interface Song {
  title: string;
  artist: string;
  album: string;
  albumArtPath: string;
  song: any;
  songPath: string;
  playlists: string[];
}

export interface State {
  currentlyPlaying: Song;
  playlist: Song[];
  playingStatus: boolean;
}

// Action types

export interface FetchPlaylistAction {
  type: typeof FETCH_PLAYLIST;
}

export interface FetchPlaylistFailureAction {
  type: typeof FETCH_PLAYLIST_FAILURE;
  payload: {
    error: any;
  };
}

export interface FetchPlaylistStartedAction {
  type: typeof FETCH_PLAYLIST_STARTED;
  payload: Song[];
}

export interface FetchPlaylistSuccessAction {
  type: typeof FETCH_PLAYLIST_SUCCESS;
  payload: Song[];
}

export interface ChangeSongAction {
  type: typeof CHANGE_SONG;
  payload: Song;
}

export interface PlayingStatusAction {
  type: typeof TOGGLE_PLAYING;
}

export interface UpdatePlaylistAction {
  type: typeof UPDATE_PLAYLIST;
  payload: {
    data: Song[];
  };
}

export type PlaylistActions =
  | FetchPlaylistAction
  | FetchPlaylistFailureAction
  | FetchPlaylistSuccessAction
  | UpdatePlaylistAction;
