import React, { ReactElement, SyntheticEvent, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPause,
  faPlay,
  faStepBackward,
  faStepForward
} from "@fortawesome/free-solid-svg-icons";
import { connect, ConnectedProps } from "react-redux";
import { ChangeSong, State } from "../store/types";
import ReactAudioPlayer from "react-audio-player";
import { changeSong, togglePlaying } from "../store/actions/currentSongActions";

const mapDispatchToProps = (dispatch: any) => ({
  togglePlaying: (playing?: boolean) => dispatch(togglePlaying(playing)),
  changeSong: (type: ChangeSong) => dispatch(changeSong(type))
});

const mapStateToProps = (state: State) => ({
  isPlaying: state.currentSong.isPlaying,
  songId: state.currentSong.data?._id
});

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const Controls: React.FC<PropsFromRedux> = ({
  isPlaying,
  changeSong,
  songId,
  togglePlaying
}): ReactElement => {
  const [songLength, setSongLength] = useState(0);
  const [songTime, setSongTime] = useState(0);
  let audioPlayer: ReactAudioPlayer;

  const onSongLoad = (data: SyntheticEvent<HTMLAudioElement, Event>) => {
    if (audioPlayer.audioEl.current)
      setSongLength(audioPlayer.audioEl.current.duration);
  };

  const getLengthInMinutes = (): string => {
    const tempDate = new Date(Math.round(songLength) * 1000);
    const minutes = tempDate.getMinutes();
    const seconds = tempDate.getSeconds();

    return (
      minutes.toString().padStart(2, "0") +
      ":" +
      seconds.toString().padStart(2, "0")
    );
  };

  const playStatusHandler = () => {
    if (audioPlayer?.audioEl?.current) {
      togglePlaying();
      audioPlayer.audioEl.current[isPlaying ? "pause" : "play"]();
    }
  };

  return (
    <div
      style={{
        height: "10%",
        width: "90%",
        position: "absolute",
        bottom: 0,
        padding: "4% 5%"
      }}
    >
      <ReactAudioPlayer
        ref={(element: ReactAudioPlayer) => (audioPlayer = element)}
        src={songId ? `/api/song/${songId}` : ""}
        listenInterval={1000}
        onListen={(e) => setSongTime(e)}
        onLoadedMetadata={(data) => onSongLoad(data)}
      />
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <div style={{ fontSize: "48px" }} onClick={() => changeSong("PREV")}>
          <FontAwesomeIcon icon={faStepBackward} />
        </div>
        <div
          style={{ fontSize: "96px", margin: "0 96px" }}
          onClick={playStatusHandler}
        >
          <FontAwesomeIcon icon={isPlaying ? faPause : faPlay} />
        </div>
        <div style={{ fontSize: "48px" }} onClick={() => changeSong("NEXT")}>
          <FontAwesomeIcon icon={faStepForward} />
        </div>
      </div>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginTop: "48px",
          fontSize: "32px"
        }}
      >
        <div>00:00</div>
        <input
          type="range"
          style={{ width: "100%", margin: "0 24px" }}
          min="0"
          max={songLength}
          value={songTime}
          onMouseUp={e => console.log(e)}
        />
        <div>{getLengthInMinutes()}</div>
      </div>
    </div>
  );
};

export default connector(Controls);
