import { CurrentSongActions, CurrentSongState, TOGGLE_PLAYING } from "../types";

const initialState = {
  isPlaying: false,
  song: null,
  albumArt: null,
  data: {}
};

export const currentSong = (
  state: CurrentSongState = initialState,
  action: CurrentSongActions
) => {
  switch (action.type) {
    case SET_SONG:
      return action;
    case TOGGLE_PLAYING:
      return {
        ...state,
        isPlaying: !state.isPlaying
      };
    default: {
      return state;
    }
  }
};

export {};
