import { PlayingStatusAction, TOGGLE_PLAYING } from "../types";

export const playingStatus = (
  state: boolean = false,
  action: PlayingStatusAction
) => {
  switch (action.type) {
    case TOGGLE_PLAYING: {
      return !state;
    }
    default: {
      return state;
    }
  }
};
