import { Song, UPDATE_PLAYLIST, UpdatePlaylistAction } from "../types";

export const playlist = (
  state: Song[] | [] = [],
  action: UpdatePlaylistAction
) => {
  switch (action.type) {
    case UPDATE_PLAYLIST: {
      return action.payload;
    }
    default: {
      return state;
    }
  }
};
