import { Dispatch } from "redux";
import FastAverageColor from "fast-average-color";
import {
  Background,
  CurrentSongActions,
  SET_BACKGROUND,
  SET_SONG,
  Song,
  State,
  TOGGLE_PLAYING
} from "../types";

export const setCurrentSong = (data: Song): CurrentSongActions => {
  return {
    type: SET_SONG,
    data
  };
};

export const setBackground = (data: Background): CurrentSongActions => ({
  type: SET_BACKGROUND,
  data
});

export const togglePlaying = (isPlaying?: boolean): CurrentSongActions => ({
  type: TOGGLE_PLAYING,
  isPlaying
});

export const changeSong = (song: Song, albumId?: string) => {
  return (dispatch: Dispatch, getState: () => State) => {
    const state = getState();
    const songId = id ? id : state.currentSong.data?._id;
    let playlistIndex = -1;

    state.playlist.data?.forEach(({ _id }: { _id: string }, index: number) => {
      if (songId === _id) playlistIndex = index;
    });
    if (
      state.playlist.data &&
      playlistIndex !== -1 &&
      type === "NEXT" &&
      playlistIndex < state.playlist.data.length - 1
    ) {
      dispatch(setCurrentSong(state.playlist?.data[playlistIndex + 1]));
    } else if (
      state.playlist.data &&
      playlistIndex !== -1 &&
      type === "PREV" &&
      playlistIndex > 0
    ) {
      dispatch(setCurrentSong(state.playlist?.data[playlistIndex - 1]));
    } else if (id && state.playlist.data && playlistIndex !== -1) {
      dispatch(setCurrentSong(state.playlist?.data[playlistIndex]));
    }
  };
};

export const getBackground = ({ id }: { id: string }) => {
  return (dispatch: Dispatch) => {
    const fac = new FastAverageColor();

    fac
      .getColorAsync(`/api/album/art/${id}`)
      .then((color: IFastAverageColorResult) => {
        const { isLight, isDark, hex: colour } = color;
        dispatch(setBackground({ isLight, isDark, colour }));
      })
      .catch((error: Error) => {
        console.error(error.message);
      });
  };
};
